import React, { useState } from 'react';
import { EmailInputField, PasswordInputField, PasswordConfirmInputField, NicknameInputField } from './InputField';
import ToastMessage from "./ToastMessage";

const SignUpForm = () => {
    const [successLabel, setSuccessLabel] = useState('');
    const [errorLabel, setErrorLabel] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [nickname, setNickname] = useState('');
    const [profileImg, setProfileImg] = useState('');

    const [emailError, setEmailError] = useState('');
    const [passwordError, setPasswordError] = useState('');
    const [confirmPasswordError, setConfirmPasswordError] = useState('');
    const [nicknameError, setNicknameError] = useState('');
    const [uploadError, setUploadError] = useState('');
    const [previewSrc, setPreviewSrc] = useState('');

    const handleEmailChange = (e) => {
        setEmail(e.target.value);
        validateEmail(e.target.value);
    };

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
        validatePassword(e.target.value);
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
        validateConfirmPassword(password, e.target.value);
    };

    const handleNicknameChange = (e) => {
        setNickname(e.target.value);
        validateNickname(e.target.value);
    };

    const handleFileChange = async (e) => {
        const file = e.target.files[0];

        // Display a preview of the selected image
        const reader = new FileReader();
        reader.onloadend = () => {
            setPreviewSrc(reader.result);
        };
        reader.readAsDataURL(file);

        // Automatically upload the image
        await handleUpload(file);
    };

    const clearLabels = () => {
        setSuccessLabel('');
        setErrorLabel('');
    }

    const handleUpload = async (file) => {
        if (!file) {
            setUploadError('ÌååÏùºÏùÑ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.');
            return;
        }

        const formData = new FormData();
        formData.append('profileimg', file);

        try {
            const response = await fetch('http://localhost:3001/api/register/profileimg', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const data = await response.json();
                setProfileImg(data.profileimg);
                setUploadError('');
            } else {
                const errorText = await response.text();
                setUploadError(`Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ïã§Ìå®: ${errorText}`);
            }
        } catch (error) {
            console.error('Error uploading the image:', error);
            setUploadError('Ïù¥ÎØ∏ÏßÄ ÏóÖÎ°úÎìú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
        }
    };

    const validateEmail = async (email) => {
        if (!email) {
            setEmailError('*Ïù¥Î©îÏùºÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (!/\S+@\S+\.\S+/.test(email)) {
            setEmailError('*Ïò¨Î∞îÎ•∏ Ïù¥Î©îÏùº Ï£ºÏÜå ÌòïÏãùÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî. (Ïòà: 123@example.com)');
        } else {
            // Check for duplicate email
            try {
                const response = await fetch('http://localhost:3001/api/accounts');
                const data = await response.json();
                const isDuplicate = data.users.some(user => user.email === email);
                if (isDuplicate) {
                    setEmailError('*Ï§ëÎ≥µÎêú Ïù¥Î©îÏùºÏûÖÎãàÎã§.');
                } else {
                    setEmailError('');
                }
            } catch (error) {
                console.error('Error checking email duplication:', error);
                setEmailError('Ïù¥Î©îÏùº Ï§ëÎ≥µ ÌôïÏù∏ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
            }
        }
    };

    const validatePassword = (password) => {
        const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[!@#$%^&*]).{8,20}$/;
        if (!password) {
            setPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (!passwordPattern.test(password)) {
            setPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 8Ïûê Ïù¥ÏÉÅ, 20Ïûê Ïù¥ÌïòÏù¥Î©∞, ÎåÄÎ¨∏Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏ÏûêÎ•º Í∞ÅÍ∞Å ÏµúÏÜå 1Í∞ú Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.');
        } else {
            setPasswordError('');
        }
    };

    const validateConfirmPassword = (password, confirmPassword) => {
        if (!confirmPassword) {
            setConfirmPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (password !== confirmPassword) {
            setConfirmPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
        } else {
            setConfirmPasswordError('');
        }
    };

    const validateNickname = (nickname) => {
        if (!nickname) {
            setNicknameError('*ÎãâÎÑ§ÏûÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (/\s/.test(nickname)) {
            setNicknameError('*ÎãâÎÑ§ÏûÑÏóêÎäî ÎùÑÏñ¥Ïì∞Í∏∞Î•º Ìè¨Ìï®Ìï† Ïàò ÏóÜÏäµÎãàÎã§.');
        } else if (nickname.length > 10) {
            setNicknameError('*ÎãâÎÑ§ÏûÑÏùÄ ÏµúÎåÄ 10Ïûê ÍπåÏßÄ ÏûëÏÑ± Í∞ÄÎä•Ìï©ÎãàÎã§.');
        } else {
            setNicknameError('');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        await validateEmail(email);
        validatePassword(password);
        validateConfirmPassword(password, confirmPassword);
        validateNickname(nickname);

        if (!emailError && !passwordError && !confirmPasswordError && !nicknameError) {
            try {
                const response = await fetch('http://localhost:3001/api/register', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                    body: JSON.stringify({ email, password, nickname, profileimg: profileImg }),
                });

                if (response.ok) {
                    setSuccessLabel('ü•ë ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ!');
                    // alert('ÌöåÏõêÍ∞ÄÏûÖ ÏÑ±Í≥µ!');
                } else {
                    // const errorText = await response.text();
                    setErrorLabel(`ü•ë ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®`);
                    // alert(`ÌöåÏõêÍ∞ÄÏûÖ Ïã§Ìå®: ${errorText}`);
                }
            } catch (error) {
                console.error('Error during registration:', error);
                alert('ÌöåÏõêÍ∞ÄÏûÖ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
            }
        } else {
            alert('ÏûÖÎ†• Ï†ïÎ≥¥Î•º ÌôïÏù∏Ìï¥Ï£ºÏÑ∏Ïöî.');
        }
    };

    return (
        <form className="SignupForm" onSubmit={handleSubmit}>
            <div className="Text32" style={{marginBottom: '51px'}}>Sign Up</div>

            <div className="SignUpProfilePickerContainer">
                <div className="SignUpProfileLabel"><span>ÌîÑÎ°úÌïÑ ÏÇ¨ÏßÑ</span></div>
                <div className="SignUpProfileImgPicker">
                    <input
                        type="file"
                        accept="image/*"
                        id="profileImgInput"
                        onChange={handleFileChange}
                        style={{display: 'none'}}
                    />
                    <label htmlFor="profileImgInput" style={{
                        width: '149px',
                        height: '149px',
                        borderRadius: '50%',
                        display: 'flex',
                        alignItems: 'center',
                        justifyContent: 'center',
                        backgroundColor: '#C4C4C4',
                        cursor: 'pointer',
                        position: 'relative',
                        overflow: 'hidden'
                    }}>
                        {previewSrc ? (
                            <img
                                src={previewSrc}
                                alt="Profile Preview"
                                style={{
                                    width: '149px',
                                    height: '149px',
                                    borderRadius: '50%',
                                    objectFit: 'cover',
                                }}
                            />
                        ) : (
