import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { PasswordConfirmInputField, PasswordInputField } from './InputField';
import ToastMessage from './ToastMessage';
import * as Buttons from "./Buttons";

const PasswordChangeForm = () => {
    const [successLabel, setSuccessLabel] = useState('');
    const [errorLabel, setErrorLabel] = useState('');
    const { userId } = useParams(); // Get userId from URL parameters

    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    const [passwordError, setPasswordError] = useState('');
    const [confirmPasswordError, setConfirmPasswordError] = useState('');

    const handlePasswordChange = (e) => {
        setPassword(e.target.value);
        validatePassword(e.target.value);
    };

    const handleConfirmPasswordChange = (e) => {
        setConfirmPassword(e.target.value);
        validateConfirmPassword(password, e.target.value);
    };

    const validatePassword = (password) => {
        const passwordPattern = /^(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,20}$/;
        if (!password) {
            setPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (!passwordPattern.test(password)) {
            setPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Îäî 8Ïûê Ïù¥ÏÉÅ, 20Ïûê Ïù¥ÌïòÏù¥Î©∞, ÎåÄÎ¨∏Ïûê, ÏÜåÎ¨∏Ïûê, Ïà´Ïûê, ÌäπÏàòÎ¨∏ÏûêÎ•º Í∞ÅÍ∞Å ÏµúÏÜå 1Í∞ú Ìè¨Ìï®Ìï¥Ïïº Ìï©ÎãàÎã§.');
        } else {
            setPasswordError('');
        }
    };

    const validateConfirmPassword = (password, confirmPassword) => {
        if (!confirmPassword) {
            setConfirmPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏ÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
        } else if (password !== confirmPassword) {
            setConfirmPasswordError('*ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏùºÏπòÌïòÏßÄ ÏïäÏäµÎãàÎã§.');
        } else {
            setConfirmPasswordError('');
        }
    };

    const clearLabels = () => {
        setSuccessLabel('');
        setErrorLabel('');
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validate before submitting
        validatePassword(password);
        validateConfirmPassword(password, confirmPassword);

        if (passwordError || confirmPasswordError) {
            setErrorLabel(passwordError || confirmPasswordError);
            return;
        }

        try {
            const response = await fetch(`http://localhost:3001/api/accounts/${userId}/password`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ password }), // Update the password in the request body
                credentials: 'include'
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(errorMessage);
            }

            setSuccessLabel('ü•ë ÎπÑÎ∞ÄÎ≤àÌò∏ ÏàòÏ†ïÏù¥ ÏôÑÎ£åÎêòÏóàÏäµÎãàÎã§.');
        } catch (error) {
            console.error('Error updating password:', error);
            setErrorLabel(`Error: ${error.message}`);
        }
    };

    return (
        <form className="PasswordChangeForm" onSubmit={handleSubmit}>
            <PasswordInputField
                // label="ÎπÑÎ∞ÄÎ≤àÌò∏*"
                value={password}
                onChange={handlePasswordChange}
                error={passwordError}
            />
            <PasswordConfirmInputField
                // label="ÎπÑÎ∞ÄÎ≤àÌò∏ ÌôïÏù∏*"
                value={confirmPassword}
                onChange={handleConfirmPasswordChange}
                error={confirmPasswordError}
            />
            <div className="PasswordBtnContainer">
                <Buttons.SubmitBtn
                    label={"ÏàòÏ†ïÌïòÍ∏∞"}
                    type="submit"
                />
                <a href="/main" className="Text14" style={{marginTop: '10px', fontWeight: '500'}}>Î©îÏù∏ÏúºÎ°ú Ïù¥Îèô</a>
            </div>
            <ToastMessage
                successLabel={successLabel}
                errorLabel={errorLabel}
                clearLabels={clearLabels}
            />
        </form>
    );
};

export default PasswordChangeForm;
